Bash(find src -name "*.js" -type f -exec perl -i -pe '
# Replace .url_small, .url_medium, .url_large with .url
s/\\.url_small/.url/g;
s/\\.url_medium/.url/g;
s/\\.url_large/.url/g;

# Replace [""url_small"", ""url_medium"", ""url_large""] with [""url""]
s/\\[""url_small"",\\s*""url_medium"",\\s*""url_large""\\]/[""url""]/g;

# Replace attributes: [""..."", ""url_small"", ... ] patterns
s/""url_small"",?\\s*//g;
s/""url_medium"",?\\s*//g;
s/""url_large"",?\\s*//g;

# Replace ||url patterns \(fallbacks\)
s/\\|\\|\\s*[a-zA-Z_\\.]+\\.url//g;

# Replace photo.width and photo.height
s/photo\\.width/null/g;
s/photo\\.height/null/g;
s/width:\\s*null,?\\s*$//g;
s/height:\\s*null,?\\s*$//g;
' {} ;)
